mapper.update:
update d_member
set m_nick = #{m_nick}, m_name = #{m_name}, m_pwd = #{m_pwd}, m_phone = #{m_phone}
where m_no = #{m_no}

mapper.select:
select *
from d_member
where m_name like #{m_name} and m_phone like #{m_phone}

select *
from d_member
where m_name like #{m_name} and m_phone like #{m_phone} and m_birth like #{m_birth}

mapper.delete:
delete 
from d_member
where m_email = #{m_email} and m_pwd = #{m_pwd}
